// Validate OAuth response and initialize pagination
const items = $input.all();
const response = items[0].json;

// Check if we got a valid access token
if (!response.access_token) {
  throw new Error('Failed to obtain access token from Shopware API');
}

console.log('‚úÖ Successfully obtained OAuth token');
console.log(`Token type: ${response.token_type}`);
console.log(`Expires in: ${response.expires_in} seconds`);

// Set initial pagination parameters
const paginationData = {
  page: 1,
  limit: 10, // Reduced for testing
  totalPages: null,
  accessToken: response.access_token,
  allProducts: [],
  startTime: new Date().toISOString(),
  errors: []
};

return [{
  json: paginationData
}];

// Process the API response with comprehensive error handling
const items = $input.all();
const response = items[0].json;

// Validate API response structure
if (!response || typeof response !== 'object') {
  throw new Error('Invalid API response format');
}

// Extract products and pagination info with fallbacks
const products = response.data || [];
const total = response.total || 0;
const currentPage = response.page || 1;
const limit = response.limit || 10;
const totalPages = Math.ceil(total / limit);

// Get existing data from previous iterations
let allProducts = $node["Validate Token & Initialize"].json.allProducts || [];
let errors = $node["Validate Token & Initialize"].json.errors || [];

// Validate products data
if (!Array.isArray(products)) {
  const error = `Page ${currentPage}: Products data is not an array`;
  errors.push(error);
  console.warn('‚ö†Ô∏è ' + error);
} else {
  // Add current page products to the collection
  allProducts = allProducts.concat(products);
  console.log(`üì¶ Fetched page ${currentPage}/${totalPages} - ${products.length} products (Total: ${allProducts.length}/${total})`);
}

// Determine if we need to fetch more pages (limit to 2 pages for testing)
const hasMorePages = currentPage < Math.min(totalPages, 2);

if (hasMorePages) {
  // Return data for next page
  return [{
    json: {
      page: currentPage + 1,
      limit: limit,
      totalPages: totalPages,
      accessToken: $node["Validate Token & Initialize"].json.accessToken,
      allProducts: allProducts,
      hasMorePages: true,
      currentTotal: allProducts.length,
      expectedTotal: total,
      startTime: $node["Validate Token & Initialize"].json.startTime,
      errors: errors
    }
  }];
} else {
  // All pages fetched, return final collection
  const endTime = new Date().toISOString();
  const duration = new Date(endTime) - new Date($node["Validate Token & Initialize"].json.startTime);

  console.log(`üéâ Pagination complete! Fetched ${allProducts.length} products in ${Math.round(duration/1000)}s`);

  if (errors.length > 0) {
    console.warn(`‚ö†Ô∏è ${errors.length} errors encountered during fetch:`);
    errors.forEach(error => console.warn(`  - ${error}`));
  }

  return [{
    json: {
      allProducts: allProducts,
      hasMorePages: false,
      totalProducts: allProducts.length,
      paginationComplete: true,
      startTime: $node["Validate Token & Initialize"].json.startTime,
      endTime: endTime,
      duration: Math.round(duration/1000),
      errors: errors
    }
  }];
}

// Transform products for vector storage with enhanced error handling
const items = $input.all();
const allProducts = items[0].json.allProducts;
const previousErrors = items[0].json.errors || [];

if (!Array.isArray(allProducts)) {
  throw new Error('Products data is not an array');
}

console.log(`üîÑ Starting transformation of ${allProducts.length} products...`);

const transformedProducts = [];
const transformErrors = [];

allProducts.forEach((product, index) => {
  try {
    // Validate required fields
    if (!product || typeof product !== 'object') {
      transformErrors.push(`Product ${index}: Invalid product object`);
      return;
    }

    if (!product.id) {
      transformErrors.push(`Product ${index}: Missing product ID`);
      return;
    }

    // Safely extract and combine text fields
    const textParts = [];

    // Extract name from translations or direct field
    let productName = product.name;
    if (!productName && product.translations && Array.isArray(product.translations)) {
      const translation = product.translations.find(t => t.name);
      productName = translation?.name;
    }
    if (productName && typeof productName === 'string') {
      textParts.push(productName.trim());
    }

    // Extract description from translations or direct field
    let productDescription = product.description;
    if (!productDescription && product.translations && Array.isArray(product.translations)) {
      const translation = product.translations.find(t => t.description);
      productDescription = translation?.description;
    }
    if (productDescription && typeof productDescription === 'string') {
      // Strip HTML tags from description
      const cleanDescription = productDescription.replace(/<[^>]*>/g, ' ').replace(/\s+/g, ' ').trim();
      if (cleanDescription.length > 0) {
        textParts.push(cleanDescription);
      }
    }

    // Add EAN if available
    if (product.ean && typeof product.ean === 'string') {
      textParts.push(`EAN: ${product.ean.trim()}`);
    }

    // Add manufacturer number if available
    if (product.manufacturerNumber && typeof product.manufacturerNumber === 'string') {
      textParts.push(`MFG: ${product.manufacturerNumber.trim()}`);
    }

    // Combine all text parts
    const textContent = textParts.filter(part => part.length > 0).join(' | ');

    // For products with minimal content, create basic description
    let finalTextContent = textContent;
    if (finalTextContent.trim().length === 0) {
      finalTextContent = `Product ID: ${product.id}`;
      if (product.ean) finalTextContent += ` | EAN: ${product.ean}`;
      if (product.active) finalTextContent += ' | Active Product';
    }

    // Safely extract price information
    let price = 0;
    let currency = 'EUR';
    if (Array.isArray(product.price) && product.price.length > 0) {
      const priceObj = product.price[0];
      if (priceObj && typeof priceObj.gross === 'number') {
        price = priceObj.gross;
      }
      if (priceObj && priceObj.currencyId) {
        currency = priceObj.currencyId;
      }
    }

    // Prepare metadata with safe defaults
    const metadata = {
      id: product.id,
      name: productName || '',
      price: price,
      currency: currency,
      stock: typeof product.stock === 'number' ? product.stock : 0,
      active: Boolean(product.active),
      manufacturerNumber: product.manufacturerNumber || '',
      ean: product.ean || '',
      createdAt: product.createdAt || null,
      updatedAt: product.updatedAt || null
    };

    transformedProducts.push({
      pageContent: finalTextContent,
      metadata: metadata
    });

  } catch (error) {
    transformErrors.push(`Product ${product.id || index}: ${error.message}`);
  }
});

console.log(`‚úÖ Transformed ${transformedProducts.length} products successfully`);
if (transformErrors.length > 0) {
  console.warn(`‚ö†Ô∏è ${transformErrors.length} transformation errors:`);
  transformErrors.slice(0, 5).forEach(error => console.warn(`  - ${error}`));
}

// Return transformed products with error tracking
const result = transformedProducts.map(product => ({ 
  json: {
    ...product,
    _metadata: {
      totalErrors: previousErrors.length + transformErrors.length,
      transformErrors: transformErrors.length
    }
  }
}));

if (result.length === 0) {
  throw new Error('No products could be transformed successfully');
}

return result;

// Enhanced completion logging with Qdrant storage results
const items = $input.all();
const totalProcessed = items.length;
const successfulInserts = items.filter(item => item.json && item.json.status === 'ok').length;
const failedInserts = totalProcessed - successfulInserts;

const completionTime = new Date().toISOString();

// Log completion status
console.log('üéâ ===== IMPORT COMPLETED =====');
console.log(`üìä Products processed: ${totalProcessed}`);
console.log(`‚úÖ Successfully stored in Qdrant: ${successfulInserts}`);
if (failedInserts > 0) {
  console.log(`‚ùå Failed to store: ${failedInserts}`);
}
console.log(`‚è∞ Completion time: ${completionTime}`);
console.log('================================');

// Check for any errors in the Qdrant responses
const errors = [];
items.forEach((item, index) => {
  if (item.json && item.json.status !== 'ok') {
    errors.push(`Item ${index}: ${item.json.message || 'Unknown error'}`);
  }
});

if (errors.length > 0) {
  console.warn('‚ö†Ô∏è Qdrant storage errors:');
  errors.slice(0, 5).forEach(error => console.warn(`  - ${error}`));
}

return [{
  json: {
    status: 'completed',
    totalProcessed: totalProcessed,
    successfulInserts: successfulInserts,
    failedInserts: failedInserts,
    errorRate: totalProcessed > 0 ? (failedInserts / totalProcessed * 100).toFixed(2) + '%' : '0%',
    message: `Successfully processed ${totalProcessed} products, ${successfulInserts} stored in Qdrant`,
    timestamp: completionTime,
    errors: errors.slice(0, 10) // Keep first 10 errors for debugging
  }
}];
