// Generate deterministic mock embedding based on content
function generateMockEmbedding(text, dimensions = 3072) {
  // Simple hash-based deterministic embedding generation
  let hash = 0;
  for (let i = 0; i < text.length; i++) {
    const char = text.charCodeAt(i);
    hash = ((hash << 5) - hash) + char;
    hash = hash & hash; // Convert to 32bit integer
  }

  const embedding = [];
  for (let i = 0; i < dimensions; i++) {
    // Use hash and index to generate deterministic values
    const seed = hash + i;
    const value = (Math.sin(seed) * 10000) % 2 - 1; // Range -1 to 1
    embedding.push(value);
  }

  return embedding;
}

