// Load and enhance dataset with Shopware-specific metadata
const fs = require('fs');
const path = require('path');

try {
  // Load the optimized dataset
  const datasetPath = path.join(process.cwd(), 'optimized_dataset_format.json');
  const datasetContent = fs.readFileSync(datasetPath, 'utf8');
  const dataset = JSON.parse(datasetContent);

  console.log('üìÅ Loading HELD Inuit Heizhandschuh optimized dataset...');
  console.log(`Product: ${dataset.dataset_metadata.product_name}`);
  console.log(`Article Number: ${dataset.dataset_metadata.product_id}`);
  console.log(`Version: ${dataset.dataset_metadata.version}`);
  console.log(`Total chunks: ${dataset.dataset_metadata.total_chunks}`);

  const chunks = dataset.optimized_chunks || [];
  const articleNumber = dataset.dataset_metadata.product_id;

  if (chunks.length === 0) {
    throw new Error('No chunks found in dataset');
  }

  // Return each chunk as a separate item with enhanced Shopware metadata
  return chunks.map((chunk, index) => ({
    json: {
      chunk_id: chunk.chunk_id,
      content: chunk.content,
      metadata: {
        ...chunk.metadata,
        // Shopware Integration Fields
        article_number: articleNumber,
        shopware_product_id: `sw_${articleNumber}`,
        shopware_variant_id: `sw_var_${articleNumber}_${index.toString().padStart(3, '0')}`,
        last_updated: new Date().toISOString(),
        content_version: '2.0',
        chunk_index: index,

        // Product Hierarchy
        brand: 'HELD',
        category_path: 'Handschuhe > Touring-Handschuhe > mit Membrane',
        product_line: 'Inuit',

        // Update Tracking
        source_system: 'shopware',
        sync_status: 'active',
        price_currency: 'EUR',
        stock_status: 'available',

        // Processing metadata
        processed_at: new Date().toISOString(),
        content_length: chunk.content.length,
        embedding_model: 'text-embedding-3-large',
        workflow_version: '2.0_shopware_optimized'
      },
      article_number: articleNumber,
      chunk_index: index,
      dataset_info: {
        product_id: dataset.dataset_metadata.product_id,
        product_name: dataset.dataset_metadata.product_name,
        language: dataset.dataset_metadata.language,
        embedding_model: dataset.dataset_metadata.embedding_model
      }
    }
  }));

} catch (error) {
  console.error('‚ùå Failed to load dataset:', error.message);
  throw new Error(`Dataset loading failed: ${error.message}`);
}

// Create payload indexes for efficient Shopware queries
const indexesToCreate = [
  'metadata.article_number',
  'metadata.shopware_product_id',
  'metadata.brand',
  'metadata.category_path',
  'metadata.last_updated',
  'metadata.chunk_type',
  'metadata.source_system',
  'metadata.sync_status'
];

console.log('üîß Creating payload indexes for Shopware optimization...');

return indexesToCreate.map(fieldName => ({
  json: {
    field_name: fieldName,
    field_schema: 'keyword'
  }
}));

// Generate deterministic mock embeddings for testing
const item = $input.item(0).json;

if (!item.content) {
  throw new Error('No content to generate embeddings for');
}

console.log(`üîÑ Generating mock embedding for chunk: ${item.chunk_id}`);

// Generate deterministic mock embedding based on content
function generateMockEmbedding(text, dimensions = 3072) {
  const crypto = require('crypto');
  const hash = crypto.createHash('md5').update(text).digest('hex');

  const embedding = [];
  for (let i = 0; i < dimensions; i++) {
    const hexPart = hash[(i * 2) % hash.length] + hash[((i * 2) + 1) % hash.length];
    const value = (parseInt(hexPart, 16) / 255.0) * 2 - 1;
    embedding.push(value);
  }

  return embedding;
}

const mockEmbedding = generateMockEmbedding(item.content);

console.log(`‚úÖ Generated mock embedding: ${mockEmbedding.length} dimensions`);
console.log(`   Range: ${Math.min(...mockEmbedding).toFixed(3)} to ${Math.max(...mockEmbedding).toFixed(3)}`);

return {
  json: {
    ...item,
    embedding: mockEmbedding,
    embedding_info: {
      model: 'mock-deterministic',
      dimensions: mockEmbedding.length,
      generated_at: new Date().toISOString(),
      fallback_used: true
    }
  }
};

// Prepare optimized Qdrant points with deterministic IDs for upsert
const item = $input.item(0).json;

if (!item.embedding || !Array.isArray(item.embedding)) {
  throw new Error('No valid embedding found');
}

if (!item.chunk_id || !item.content) {
  throw new Error('Missing required chunk data');
}

// Generate deterministic Point ID based on article number and chunk index
function generateDeterministicPointId(articleNumber, chunkIndex) {
  const idString = `${articleNumber}_${chunkIndex.toString().padStart(3, '0')}`;
  const crypto = require('crypto');
  const hash = crypto.createHash('md5').update(idString).digest('hex');
  // Convert to positive integer within safe range
  return parseInt(hash.substring(0, 15), 16);
}

const pointId = generateDeterministicPointId(item.article_number, item.chunk_index);

// Prepare comprehensive payload for Shopware integration
const payload = {
  chunk_id: item.chunk_id,
  content: item.content,
  metadata: item.metadata
};

// Create Qdrant point
const qdrantPoint = {
  id: pointId,
  vector: item.embedding,
  payload: payload
};

console.log(`üîÑ Preparing Qdrant point: ${pointId}`);
console.log(`   Article: ${item.article_number}`);
console.log(`   Chunk: ${item.chunk_id}`);
console.log(`   Vector dimensions: ${item.embedding.length}`);

return {
  json: {
    point: qdrantPoint,
    article_number: item.article_number,
    chunk_index: item.chunk_index,
    summary: {
      point_id: pointId,
      chunk_type: item.metadata.chunk_type,
      content_category: item.metadata.content_category,
      article_number: item.article_number,
      shopware_product_id: item.metadata.shopware_product_id
    }
  }
};

// Delete existing product data before upsert
const items = $input.all();

if (!items || items.length === 0) {
  throw new Error('No points to process');
}

// Get article number from first item
const articleNumber = items[0].json.article_number;

console.log(`üóëÔ∏è Deleting existing data for article: ${articleNumber}`);

// Prepare delete filter
const deleteFilter = {
  must: [
    {
      key: 'metadata.article_number',
      match: {
        value: articleNumber
      }
    }
  ]
};

return [{
  json: {
    filter: deleteFilter,
    article_number: articleNumber,
    points_to_upsert: items.map(item => item.json.point)
  }
}];

// Test upsert functionality by performing a search
const upsertResult = $input.item(0).json;
