console.log('üß™ Testing upsert functionality...');
console.log(`Article Number: ${articleNumber}`);
console.log(`Upsert Status: ${upsertResult.status}`);

// Prepare test search queries
const testQueries = [
  'Wie lange h√§lt der Akku?',
  'Welche Gr√∂√üen gibt es?',
  'Ist der Handschuh wasserdicht?',
  'Preis des Handschuhs'
];

return testQueries.map(query => ({
  json: {
    query: query,
    article_number: articleNumber,
