  }

  return embedding;
}

const queryEmbedding = generateMockEmbedding(item.query);

return {
  json: {
    ...item,
    query_vector: queryEmbedding
  }
};

// Generate final completion report
const searchResults = $input.all();
const articleNumber = searchResults[0]?.json?.article_number || 'unknown';

const completionTime = new Date().toISOString();

// Analyze search results
let totalSearches = searchResults.length;
let successfulSearches = 0;
let searchSummary = [];

searchResults.forEach((result, index) => {
  const searchData = result.json;
  const query = $node['Prepare Test Queries'].json[index]?.query || 'unknown';

  if (searchData.result && Array.isArray(searchData.result)) {
    successfulSearches++;
    searchSummary.push({
      query: query,
      results_found: searchData.result.length,
      top_score: searchData.result[0]?.score || 0
    });
  }
});

// Generate final report
const finalReport = {
  workflow_status: 'completed',
  completion_time: completionTime,

  workflow_summary: {
    article_number: articleNumber,
    collection_name: 'held_products_optimized',
    workflow_version: '2.0_shopware_optimized'
  },

  upsert_summary: {
    upsert_completed: true,
    article_processed: articleNumber,
    collection_recreated: true
  },

  search_testing: {
    total_queries: totalSearches,
    successful_searches: successfulSearches,
    success_rate: totalSearches > 0 ? `${Math.round((successfulSearches / totalSearches) * 100)}%` : '0%',
    search_results: searchSummary
  },

  optimization_features: [
    'Shopware-specific metadata enhancement',
    'Deterministic point ID generation',
    'Article number-based upsert functionality',
    'Payload indexing for efficient queries',
    'Mock embedding generation for testing',
    'Search functionality verification'
  ],

  next_steps: [
    'Integrate with real OpenAI embeddings API',
    'Connect to live Shopware product data',
    'Implement automated sync scheduling',
    'Add monitoring and alerting',
    'Scale for production workloads'
  ]
};

console.log('üéâ ===== SHOPWARE VECTORIZATION COMPLETED =====');
console.log(`üìä Article processed: ${finalReport.workflow_summary.article_number}`);
console.log(`‚úÖ Upsert completed: ${finalReport.upsert_summary.upsert_completed}`);
console.log(`üîç Search success rate: ${finalReport.search_testing.success_rate}`);
console.log(`‚è∞ Completed at: ${completionTime}`);
console.log('================================================');

return [{ json: finalReport }];
